mapper.select:
SELECT BOARD_NUM, USERNAME, BOARD_TITLE, BOARD_DATE, BOARD_CONTENTS, UPDATE_AT, BOARD_HITS FROM NOTICE
${searchField} LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY BOARD_DATE DESC, BOARD_NUM DESC LIMIT #{startRow}, #{pageSize}

mapper.update:
UPDATE NOTICE
  SET BOARD_HITS = BOARD_HITS + 1
WHERE BOARD_NUM = #{boardNum}
